mapper.select:
SELECT p.name, p.image_url, p.comment_num, p.postage, p.ship, p.sales_volume, p.`sales_mode_id`, p.`introduction`, pt.name product_type_name, p.sales_virtual, UNIX_TIMESTAMP(ap.end_time)-unix_timestamp(now()) as plusTime , s.name shopName, ap.* FROM sl_product p LEFT JOIN sl_activity_product ap ON p.id = ap.`product_id` LEFT JOIN sl_shop s ON p.`shop_id` = s.`id` LEFT JOIN sl_product_type pt ON p.product_type_id = pt.id
p.sold_out = 1 and ap.enabled = 1 and p.del = 0 AND ap.end_time>NOW()
AND p.sales_mode_id = #{salesModeId}
and ap.activity_id = #{activityId}
and p.product_type_id = #{goodsTypeId}
and pt.parent_id = #{goodsTypeId}
AND s.longitude BETWEEN #{longitudeMin} - 10 AND #{longitudeMax} AND s.latitude BETWEEN #{latitudeMin} - 10 AND #{latitudeMax}
AND ap.price >= #{priceMin}
AND ap.price <= #{priceMax}
AND ap.price BETWEEN #{priceMin} AND #{priceMax}
AND s.address like #{pAddress}
ap.price ${sortByPrice},
ap.count ${sortByCount},
ap.awward_money ${sortByAward},
p.sales_virtual ${sortBySale},
(p.sales_virtual + p.comment_num) DESC,
p.sales_virtual DESC

SELECT srp.red_packet_type, srp.result, u.id userId, u.username , u.nick_name, sp.`name` , sl.is_failure, srp.money, rpd.create_time, srp.id red_packet_id, so.id orderId FROM sl_red_packet srp LEFT JOIN sl_red_packet_detail rpd ON srp.id = red_packet_id LEFT JOIN sl_order_extend soe ON srp.order_extend_id = soe.id LEFT JOIN sl_order so ON soe.order_id = so.id LEFT JOIN sl_sharing_links sl ON sl.id = soe.service_id LEFT JOIN sl_product sp ON sl.product_id = sp.id LEFT JOIN sl_user u ON so.user_id = u.id
srp.user_id = #{userId}
and srp.result in (4,5,6)
and srp.result = #{result}

SELECT od.product_name productName, od.price price, od.quantity quantity, suu.nick_name AS buyUser, so.total_amount totalAmount, sb.`name` paymentName, od.shipping_state shippingState, od.type type, so.id orderId, rp.money money, so.pay_time payTime FROM sl_order_extend oe LEFT JOIN sl_sharing_links sl ON oe.service_id = sl.id LEFT JOIN sl_order_detail od ON oe.order_id = od.order_id LEFT JOIN sl_order so ON oe.order_id = so.id LEFT JOIN sl_base_payment sb ON sb.id = so.payment_channel LEFT JOIN sl_product sp ON sl.product_id = sp.id LEFT JOIN sl_activity_product ap ON sp.id = ap.`product_id` LEFT JOIN sl_user suu ON so.user_id = suu.id LEFT JOIN sl_red_packet rp ON oe.id = rp.order_extend_id
suu.id = #{userId}
and sl.id= #{shareId}
AND rp.red_packet_type=4

SELECT sum(rp.money) sumMoney FROM sl_sharing_links sl LEFT JOIN sl_user su ON sl.share_person_id=su.id LEFT JOIN sl_order_extend oe ON sl.id=oe.service_id LEFT JOIN sl_red_packet rp ON oe.id=rp.order_extend_id
sl.share_person_id = #{userId}
and sl.id= #{shareId}
AND rp.red_packet_type=4
